import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { postVideogame, getGenres } from '../actions/index'
import { useDispatch, useSelector } from 'react-redux';
import styles from '../Styles/VideogameCreate.module.css';

function validate(input) {
    let errors = {};
    if (!input.name) {
        errors.name = 'Name is required';
    } else if (!input.description) {
        errors.description = 'Description is required';
    } else if (!input.released) {
        errors.released = 'Release Date is required';
    } else if (!input.rating) {
        errors.rating = 'Rating is required';
    }
    return errors
}



export default function VideoGameCreate() {
    const dispatch = useDispatch();
    const history = useHistory();
    const genres = useSelector((state) => state.genres)
    const [errors, setErrors] = useState({});


    const [input, setInput] = useState({
        name: "",
        description: "",
        released: "",
        rating: "",
        platform: "",
        image: "",
        genres: []
    })

    function handleChange(e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }
    function handleCheck(e) {

        if (e.target.checked) {

            setInput({
                ...input,
                rating: e.target.value
            })

        } else {
            setInput({
                ...input,
                rating: ""
            })
        }
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    function handleSelect(e) {
        setInput({
            ...input,
            genres: [...input.genres, e.target.value]
        })
    }

    function handleDelete(g) {
        setInput({
            ...input,
            genres: input.genres.filter(genre => genre !== g)
        })
    }

    function handleSubmit(e) {
        e.preventDefault()
        // console.log(input)
        dispatch(postVideogame(input))
        alert("Videogame successfully created");
        setInput({
            name: "",
            description: "",
            released: "",
            rating: "",
            platform: "",
            image: "",
            genres: []
        })
        history.push('/home');
    }

    useEffect(() => {
        dispatch(getGenres())
    }, [dispatch]);

    return (
        <div>
            <div className={styles.navbar}>

                <Link to='/home' className={styles.link}><h1 className={styles.logo}>Game<span className={styles.logo2}>Hub</span></h1></Link>
                <Link to='/home'><button className={styles.btn}>Previous Stage</button></Link>

            </div>
                <div className={styles.father}>
                    <div className={styles.title}>
                        <h1>Create your Videogame</h1>

                    </div>

                    <form className={styles.form} onSubmit={(e) => handleSubmit(e)}>
                        <div className={styles.son}>


                            <div>
                                <label className={styles.label}>Name:</label>
                                <input
className={styles.inputText}
                                    type="text"
                                    value={input.name}
                                    name="name"
                                    onChange={(e) => handleChange(e)}
                                />
                                {errors.name && (
                                    <p className={styles.errors}>{errors.name}</p>
                                )}
                            </div>
                            <div>
                                <label>Platforms:</label>
                                <input
                                    type="text"
                                    value={input.platform}
                                    name="platform"
                                    onChange={(e) => handleChange(e)}
                                />
                            </div>
                            <div>
                                <label>Rating:</label>
                                <label>
                                    <input

                                        type="checkbox"
                                        value="1"
                                        name="1"
                                        onChange={(e) => handleCheck(e)}
                                    />1⭐</label>
                                <label>
                                    <input

                                        type="checkbox"
                                        value="2"
                                        name="2"
                                        onChange={(e) => handleCheck(e)}
                                    />2⭐</label>
                                <label>
                                    <input

                                        type="checkbox"
                                        value="3"
                                        name="3"
                                        onChange={(e) => handleCheck(e)}
                                    />3⭐</label>
                                <label>
                                    <input

                                        type="checkbox"
                                        value="4"
                                        name="4"
                                        onChange={(e) => handleCheck(e)}
                                    />4⭐</label>
                                <label>
                                    <input

                                        type="checkbox"
                                        value="5"
                                        name="5"
                                        onChange={(e) => handleCheck(e)}
                                    />5⭐</label>
                                {errors.rating && (
                                    <p className="error">{errors.rating}</p>
                                )}
                            </div>
                            <div>
                                <label>Genres:</label>
                                <select onChange={(e) => handleSelect(e)}>
                                    {genres.map((g) => (
                                        <option key={g.id} value={g.name}>{g.name}</option>
                                    ))}
                                </select>
                                {input.genres.map(g =>
                                    <div key={g}>
                                        <p>{g}</p>
                                        <button className="botonX" onClick={() => handleDelete(g)}>x</button>
                                    </div>
                                )}
                            </div>
                            <div>
                                <label>Release Date:</label>
                                <input
                                    type="text"
                                    value={input.released}
                                    name="released"
                                    onChange={(e) => handleChange(e)}
                                />
                                {errors.released && (
                                    <p className="error">{errors.released}</p>
                                )}
                            </div>
                            <div>
                                <label>Image:</label>
                                <input
                                    type="text"
                                    value={input.image}
                                    name="image"
                                    onChange={(e) => handleChange(e)}
                                />
                            </div>
                        </div>

                        <div style={{backgroundColor:"red", height:"20%"}}>
                            <div >
                                <label>Description:</label>
                                <textarea
                                    type="text"
                                    value={input.description}
                                    name="description"
                                    onChange={(e) => handleChange(e)}
                                />
                                {errors.description && (
                                    <p className="error">{errors.description}</p>
                                )}
                            </div>
                        </div>

                        <button className={styles.btn} type="submit">Create Videogame</button>

                    </form>
                </div>
        </div>
    )

}
