import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogames, filterGamesByRating, filterCreatedGames, orderByName } from '../actions';
import { Link } from 'react-router-dom';
import VgCard from './Card';
import Paged from './Paged';



export default function Home() {

    const dispatch = useDispatch();
    const allVideogames = useSelector((state) => state.videogames);
    const [order, setOrder] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [vgPerPage] = useState(15);  //cambio para quitar el warning [vgPerPage, setVgPerPage]
    const indexOfLastVideogame = currentPage * vgPerPage; //15
    const indexOfFirstVideogame = indexOfLastVideogame - vgPerPage; //0
    const currentVideoGame = allVideogames.slice(indexOfFirstVideogame, indexOfLastVideogame)

    const paged = (pageNumber) => {
        setCurrentPage(pageNumber);
    }


    useEffect(() => {
        dispatch(getVideogames())

    }, [dispatch])

    function handleClick(e) {
        e.preventDefault();
        dispatch(getVideogames());
    }

    function handleFilterRating(e) {
        dispatch(filterGamesByRating(e.target.value));
    }

    function handleFilterCreated(e) {
        dispatch(filterCreatedGames(e.target.value));
    }
    
    function handleSort(e){
        e.preventDefault();
        dispatch(orderByName(e.target.value));
        setCurrentPage(1);
        setOrder(`Ordered ${e.target.value}`);
    }

    return (
        <div>
            <Link to='/videogame'>Create your videogame</Link>
            <h1>Videogames API</h1>
            <button onClick={e => { handleClick(e) }} >
                Reload All Videogames
            </button>
            <div>

                <select onChange={e => { handleSort(e) }}>
                    <option selected="true" disabled="disabled">Sort by Name</option>
                    <option value="asc">A-Z</option>
                    <option value="desc">Z-A</option>
                </select>
                <select onChange={e => { handleFilterRating(e) }}>
                    <option selected="true" disabled="disabled">Filter by Rating</option>
                    <option value="all"> All Games </option>
                    <option value="1"> 1⭐ Games </option>
                    <option value="2"> 2⭐ Games </option>
                    <option value="3"> 3⭐ Games </option>
                    <option value="4"> 4⭐ Games </option>
                    <option value="5"> 5⭐ Games </option>
                </select>
                <select onChange={e => {handleFilterCreated(e)}}>
                    <option selected="true" disabled="disabled">Filter by Origin</option>
                    <option value="all">All games</option>
                    <option value="apig">Api games</option>
                    <option value="dbg">Database games</option>
                </select>

                <select>
                <option value='all'>Any Genre</option>
                <option value='Action'>Action</option>
                <option value='Adventure'>Adventure</option>
                <option value='Arcade'>Arcade</option>
                <option value='Board Games'>Board Games</option>
                <option value='Card'>Card</option>
                <option value='Casual'>Casual</option>
                <option value='Educational'>Educational</option>
                <option value='Family'>Family</option>
                <option value='Fighting'>Fighting</option>
                <option value='Indie'>Indie</option>
                <option value='Massively Multiplayer'>Massively Multiplayer</option>
                <option value='Platformer'>Platformer</option>
                <option value='Puzzle'>Puzzle</option>
                <option value='Racing'>Racing</option>
                <option value='RPG'>RPG</option>
                <option value='Shooter'>Shooter</option>
                <option value='Simulation'>Simulation</option>
                <option value='Sports'>Sports</option>
                <option value='Strategy'>Strategy</option>
                </select>

                <div>
                    <Paged vgPerPage={vgPerPage} allVideogames={allVideogames.length} paged={paged} />
                </div>
                {
                    currentVideoGame?.map(v =>
                        <VgCard key={v.id} name={v.name} image={v.img} genre={v.genres ? v.genres.map((v, index) => <h5 key={index}>{v}</h5>) : 'N/A'} />

                    ) //cierre de map 
                }
            </div>
        </div>
    )
}
