const initialState = {
    videogames : [],
    ratingVideogames: [],
    createdGames: []
}

function rootReducer(state = initialState, action){
    switch (action.type) {
        case 'GET_VIDEOGAMES': return {
            ...state,
            videogames: action.payload,
            ratingVideogames: action.payload,
            createdGames: action.payload,
        }
        //------------------------------------------FIN DEL GET_VIDEOGAMES---------------------------------------------------------//
        case 'FILTER_BY_RATING': 
        const ratingFiltered = action.rating === "all" ? state.ratingVideogames?.filter(vg => vg) : state.ratingVideogames?.filter(vg => Math.floor(vg.rating) === parseInt(action.rating, 10))
       return{
            ...state,
            videogames: ratingFiltered,
        }
        //------------------------------------------FIN DEL FILTER_BY_RATING---------------------------------------------------------//
        case 'FILTER_CREATED':
        const createdFilter = action.payload === 'dbg' ? state.createdGames.filter(vg => vg.createdInDb) : state.createdGames.filter(vg => !vg.createdInDb)   
        return{
            ...state,
            videogames: action.payload === 'all' ? state.createdGames : createdFilter
        }
        //------------------------------------------FIN DEL FILTER_CREATED---------------------------------------------------------//
        case 'ORDER_BY_NAME':
        let sortedArray = action.payload === 'asc' ?
            state.videogames.sort(function(a,b){
                if(a.name.toLowerCase() > b.name.toLowerCase()){
                    return 1;
                }
                if(a.name.toLowerCase() < b.name.toLowerCase()){
                    return -1;
                }
                return 0;
            }) :
            state.videogames.sort(function(a,b){
                if(a.name.toLowerCase() > b.name.toLowerCase()){
                    return -1;
                }
                if(a.name.toLowerCase() < b.name.toLowerCase()){
                    return 1;
                }
                return 0;
            })
            return {
                ...state,
                videogames: sortedArray
            }


        default: return state;
    }
}

export default rootReducer;
