const initialState = {
    videogames: [],
    ratingVideogames: [],
    allVideogames: []
}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'GET_VIDEOGAMES': return {
            ...state,
            videogames: action.payload,
            ratingVideogames: action.payload,
            allvideogames: action.payload
        }

        case 'GET_NAME_VIDEOGAMES': return{
            ...state,
            videogames: action.payload
        }

        //------------------------------------------FIN DEL GET_VIDEOGAMES---------------------------------------------------------//
        case 'FILTER_BY_RATING':
            // const ratingFiltered = state.ratingVideogames?.filter(vg => Math.floor(vg.rating) === parseInt(action.rating, 10))
            const ratingSorted = action.rating === 'btw' ?
            state.videogames.sort(function (a, b) {
                if (a.rating > b.rating) {
                    return -1;
                }
                if (a.rating < b.rating) {
                    return 1;
                }
                return 0;
            }) : state.videogames.sort(function (a, b) {
                if (a.rating > b.rating) {
                    return 1;
                }
                if (a.rating < b.rating) {
                    return -1;
                }
                return 0;
            }) 
            return {
                ...state,
                videogame: action.rating === 'all' ? state.allVideogames : ratingSorted
            }
        //------------------------------------------FIN DEL FILTER_BY_RATING---------------------------------------------------------//


        case 'FILTER_CREATED':
            const createdFilter = action.payload === 'dbg' ? state.videogames.filter(vg => vg.createdInDb) : state.videogames.filter(vg => !vg.createdInDb)
            return {
                ...state,
                videogames: action.payload === 'all' ? state.allVideogames : createdFilter
            }
        //------------------------------------------FIN DEL FILTER_CREATED---------------------------------------------------------//


        case 'ORDER_BY_NAME':
            const sortedArray = action.payload === 'asc' ?
                state.videogames.sort(function (a, b) {
                    if (a.name.toLowerCase() > b.name.toLowerCase()) {
                        return 1;
                    }
                    if (a.name.toLowerCase() < b.name.toLowerCase()) {
                        return -1;
                    }
                    return 0;
                }) :
                state.videogames.sort(function (a, b) {
                    if (a.name.toLowerCase() > b.name.toLowerCase()) {
                        return -1;
                    }
                    if (a.name.toLowerCase() < b.name.toLowerCase()) {
                        return 1;
                    }
                    return 0;
                })
            return {
                ...state,
                videogames: sortedArray
            }
        //------------------------------------------FIN DEL ORDER_BY_NAME---------------------------------------------------------//

        case 'FILTER_BY_GENRE':
            const filterVideogames = action.payload ===  'all' ? state.allVideogames : state.videogames.filter(vg => vg.genres.includes(action.payload));
            return {
                ...state,
                videogames: filterVideogames
            }

        default: return state;
    }
}

export default rootReducer;
