import React from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector} from 'react-redux';
import { getDetails } from '../actions/index';
import { useEffect } from 'react';
import {resetDetail} from '../actions/index'
import styles from '../Styles/Detail.module.css'

export default function Detail(props){

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getDetails(props.match.params.id));
        return function cleanup() {
            dispatch(resetDetail())
          }
    }, [dispatch]);

    const myVideogame = useSelector((state) => state.detail)

    return (
        <div>
        <Link to='/home'><button>Previous Stage</button></Link>
            {
                myVideogame.length > 0 ?
                <div className={styles.container}>
                    <img src={myVideogame[0].img} alt="videogame_image" width="700" height="500"/>
                    <h1>{myVideogame[0].name}</h1>
                    <h2>{myVideogame[0].genres ? myVideogame[0].genres.map(g => <li>{g}</li>)  : 'No Genres Available'}</h2>
                    <h2>{myVideogame[0].rating}</h2>
                    <h3>{myVideogame[0].platforms ? myVideogame[0].platforms.map(g => <li>{g}</li>) : 'No Platforms Available'}</h3>
                    <h3>{myVideogame[0].released}</h3>
                    {myVideogame[0].description?.replace(/<[^>]*>?/g, '')}


                </div>

                : <p>Loading...</p>
            }
        </div>
    )
}
