import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { postVideogame, getGenres } from '../actions/index'
import { useDispatch, useSelector } from 'react-redux';

function validate(input) {
    let errors = {};
    if (!input.name) {
        errors.name = 'Name is required';
    } else if (!input.description) {
        errors.description = 'Description is required';
    } else if (!input.released) {
        errors.released = 'Release Date is required';
    } else if (!input.rating) {
        errors.rating = 'Rating is required';
    }
    return errors
}



export default function VideoGameCreate() {
    const dispatch = useDispatch();
    const history = useHistory();
    const genres = useSelector((state) => state.genres)
    const [errors, setErrors] = useState({});

    const [input, setInput] = useState({
        name: "",
        description: "",
        released: "",
        rating: "",
        platform: "",
        image: "",
        genres: []
    })

    function handleChange(e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }
    function handleCheck(e) {
        if(e.target.checked === true){
            e.target.checked = false;
        }
        if (e.target.checked) {
            setInput({
                ...input,
                rating: e.target.value
            })
        console.log(input)
            

            

        }
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    function handleSelect(e) {
        setInput({
            ...input,
            genres: [...input.genres, e.target.value]
        })
    }

    function handleSubmit(e) {
        e.preventDefault()
        console.log(input)
        dispatch(postVideogame(input))
        alert("Videogame successfully created");
        setInput({
            name: "",
            description: "",
            released: "",
            rating: "",
            platform: "",
            image: "",
            genres: []
        })
        history.push('/home');
    }

    useEffect(() => {
        dispatch(getGenres())
    }, [dispatch]);

    return (
        <div>
            <Link to='/home'><button>Previous Stage</button></Link>

            <h1>Create your Videogame</h1>

            <form onSubmit={(e) => handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input
                        
                        type="text"
                        value={input.name}
                        name="name"
                        onChange={(e) => handleChange(e)}
                    />
                    {errors.name && (
                        <p className="error">{errors.name}</p>
                    )}
                </div>
                <div>
                    <label>Platforms:</label>
                    <input
                        type="text"
                        value={input.platform}
                        name="platform"
                        onChange={(e) => handleChange(e)}
                    />
                </div>
                <div>
                    <label>Rating:</label>
                    <label>
                        <input
                        
                            type="checkbox"
                            value="1"
                            name="1"
                            onChange={(e) => handleCheck(e)}
                        />1⭐</label>
                    <label>
                        <input
                           
                            type="checkbox"
                            value="2"
                            name="2"
                            onChange={(e) => handleCheck(e)}
                        />2⭐</label>
                    <label>
                        <input
                            
                            type="checkbox"
                            value="3"
                            name="3"
                            onChange={(e) => handleCheck(e)}
                        />3⭐</label>
                    <label>
                        <input
                            
                            type="checkbox"
                            value="4"
                            name="4"
                            onChange={(e) => handleCheck(e)}
                        />4⭐</label>
                    <label>
                        <input
                            
                            type="checkbox"
                            value="5"
                            name="5"
                            onChange={(e) => handleCheck(e)}
                        />5⭐</label>
                    {errors.rating && (
                        <p className="error">{errors.rating}</p>
                    )}
                </div>
                <div>
                    <label>Genres:</label>
                    <select onChange={(e) => handleSelect(e)}>
                        {genres.map((g) => (
                            <option key={g.id} value={g.name}>{g.name}</option>
                        ))}
                    </select>
                    <ul>{input.genres.map(g => <li key={g}>{g}</li>)}</ul>
                </div>
                <div>
                    <label>Release Date:</label>
                    <input
                        type="text"
                        value={input.released}
                        name="released"
                        onChange={(e) => handleChange(e)}
                    />
                    {errors.released && (
                        <p className="error">{errors.released}</p>
                    )}
                </div>
                <div>
                    <label>Image:</label>
                    <input
                        type="text"
                        value={input.image}
                        name="image"
                        onChange={(e) => handleChange(e)}
                    />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea
                        type="text"
                        value={input.description}
                        name="description"
                        onChange={(e) => handleChange(e)}
                    />
                    {errors.description && (
                        <p className="error">{errors.description}</p>
                    )}
                </div>

                <button type="submit">Create Videogame</button>
            </form>
        </div>
    )

}
