import axios from 'axios';

export function getVideogames() {
    return async function (dispatch) {
        var json = await axios.get("http://localhost:3001/videogames", {
        });
        return dispatch({
            type: 'GET_VIDEOGAMES',
            payload: json.data
        });
    }
}
export function reloadVideogames(){
    return {
        type:'RELOAD_VIDEOGAMES'
    }
}

export function filterGamesByRating(rating){
    return {
        type: 'FILTER_BY_RATING',
        rating
    }
}
