const initialState = {
    videogames : [],
}

function rootReducer(state = initialState, action){
    switch (action.type) {
        case 'GET_VIDEOGAMES': return {
            ...state,
            videogames: action.payload
        }
        case 'FILTER_BY_RATING': 
        const allVideogames = state.videogames;
        console.log(state.videogames)
        const ratingFiltered = action.payload === "allg" ? allVideogames : allVideogames.filter(vg => Math.floor(vg.rating) === action.payload)
        
        //v => v.name.toLowerCase().includes(name.toLowerCase()
        return{
            ...state.videogames,
            videogames: ratingFiltered,
        }
            
    
        default: return state;
    }
}

export default rootReducer;
