const initialState = {
    videogames : [],
    filteredVideogames: [],
}

function rootReducer(state = initialState, action){
    switch (action.type) {
        case 'GET_VIDEOGAMES': return {
            ...state,
            videogames: action.payload,
            filteredVideogames: action.payload
        }
        case 'FILTER_BY_RATING': 
        // const allVideogames = state.videogames;
        console.log('state',state.videogames)
        console.log('type',action.type)
        console.log('action.rating  ',action.rating)


        const ratingFiltered = action.rating === "all" ? state.filteredVideogames?.filter(vg => vg) : state.filteredVideogames?.filter(vg => Math.floor(vg.rating) === parseInt(action.rating, 10))


        
        console.log('ratingFiltered',ratingFiltered)
        
        //v => v.name.toLowerCase().includes(name.toLowerCase()
        return{
            ...state,
            videogames: ratingFiltered,
        }

            
    
        default: return state;
    }
}

export default rootReducer;
