const initialState = {
    videogames : [],
    allVideogames: [],
}

function rootReducer(state = initialState, action){
    switch (action.type) {
        case 'GET_VIDEOGAMES': return {
            ...state,
            videogames: action.payload,
            allVideogames: action.payload
        }
        //------------------------------------------FIN DEL GET_VIDEOGAMES---------------------------------------------------------//
        case 'FILTER_BY_RATING': 
        const ratingFiltered = action.rating === "all" ? state.allVideogames?.filter(vg => vg) : state.allVideogames?.filter(vg => Math.floor(vg.rating) === parseInt(action.rating, 10))
       return{
            ...state,
            videogames: ratingFiltered,
        }
        //------------------------------------------FIN DEL FILTER_BY_RATING---------------------------------------------------------//
        case 'FILTER_CREATED':
        const createdFilter = action.payload === 'dbg' ? state.allVideogames.filter(vg => vg.createdInDb) : state.allVideogames.filter(vg => !vg.createdInDb)   
        return{
            ...state,
            videogames: action.payload === 'all' ? state.allVideogames : createdFilter
        }
        //------------------------------------------FIN DEL FILTER_CREATED---------------------------------------------------------//
        default: return state;
    }
}

export default rootReducer;
