                            placeholder="Enter the Release Date..."
                                type="text"
                                className={styles.input}
                                value={input.released}
                                name="released"
                                onChange={(e) => handleChange(e)}
                            />
                            {errors.released && (
                                <p className={styles.error}>{errors.released}</p>
                            )}
