                        <div className={styles.son}> 
                            <label className={styles.label}>Image:</label>
                            <input
                            placeholder="Enter the Image URL..."
                            className={styles.input}
                                type="text"
                                value={input.image}
                                name="image"
                                onChange={(e) => handleChange(e)}
                            />
