import React from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector} from 'react-redux';
import { getDetails } from '../actions/index';
import { useEffect } from 'react';
import {resetDetail} from '../actions/index'
import loading from '../media/loading.jpg'
import styles from '../Styles/Detail.module.css'

export default function Detail(props){

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getDetails(props.match.params.id));
        return function cleanup() {
            dispatch(resetDetail())
          }
    }, [dispatch]);

    const myVideogame = useSelector((state) => state.detail)

    return (
        <div>
        <div>
        <div className={styles.navbar}>
        <a className={styles.logo}>Game<span className={styles.logo2}>Hub</span></a>
        <Link to='/home'><button className={styles.btn}>Previous Stage</button></Link>

        </div>

        </div>
            {
                myVideogame.length > 0 ?
                <div className={styles.container}>
                    <div>
                    <img className={styles.img} src={myVideogame[0].img} alt="videogame_image" width="700" height="500"/>
                    <div className={styles.infoContainer}>
                    <h3 className={styles.subtitles}>Genres:</h3>
                    <h3 className={styles.subtitles}>Platforms:</h3>
                    <div classname={styles.infoText}>{myVideogame[0].genres ? myVideogame[0].genres.map(g => <p>{g}</p>)  : 'No Genres Available'}</div>
                    <div classname={styles.infoText}>{myVideogame[0].platforms ? myVideogame[0].platforms.map(g => <p>{g}</p>) : 'No Platforms Available'}</div>
                    </div>
                    </div>

                    <div className={styles.info}>

                    <h2 className={styles.title}>{myVideogame[0].name}</h2>
                    <div className={styles.infoContainer}>   
                    <h3 className={styles.subtitles}>Rating: </h3>
                    <h3 className={styles.subtitles}>Release Date:</h3>
                    <p classname={styles.infoText}>{myVideogame[0].rating}</p>
                    <p classname={styles.infoText}>{myVideogame[0].released}</p>
                    </div>
                    <p className={styles.description}>{myVideogame[0].description?.replace(/<[^>]*>?/g, '')}</p>
                    </div>


                </div>

                : <img src={loading} type="jpeg" alt="Loading..."/>
            }
        </div>
    )
}
