import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogames } from '../actions';
import { Link } from 'react-router-dom';
import VgCard from './Card';
import {AiFillStar} from 'react-icons'



export default function Home() {

    const dispatch = useDispatch();
    const allVideogames = useSelector((state) => state.videogames)


    useEffect(() => {
        dispatch(getVideogames())

    }, [dispatch])

    function handleClick(e) {
        e.preventDefault();
        dispatch(getVideogames());
    }

    return (
        <div>
            <Link to='/videogame'>Create your videogame</Link>
            <h1>Videogames API</h1>
            <button onClick={e => { handleClick(e) }} >
                Reload All Videogames
            </button>
            <div>
                <select>
                    <option value="asc">Asc</option>
                    <option value="desc">Desc</option>
                </select>
                <select>
                    <option value="allg">All Games</option>
                    <option value="r1">Games rated with 1<AiFillStar/></option>
                    <option value="r2">Games rated with 2</option>
                    <option value="r3">Games rated with 3</option>
                    <option value="r4">Games rated with 4</option>
                    <option value="r5">Games rated with 5</option>
                </select>
                <select>
                    <option value="allg">All games</option>
                    <option value="apig">Api games</option>
                    <option value="dbg">Database games</option>
                </select>
                <select>
                    <option>
                        By Genres
                    </option>
                </select>
                {
                    allVideogames ? allVideogames.map(v => 
                        <VgCard key={v.id} name={v.name} image={v.img} genre={v.genres ? v.genres.map((v, index) => <h5 key={index}>{v}</h5>) : 'N/A' }/>
                    
                    ) : <h3>No hay juegos</h3> //cierre de map 
                }
            </div>
        </div>
    )
}
